import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function EventDetailsPage({ refreshEvents }) {
  const { eventId } = useParams();
  const [eventDetails, setEventDetails] = useState({});
  const [editedEvent, setEditedEvent] = useState({});

  useEffect(() => {
    // Fetch event details based on the eventId
    axios
      .get(`http://localhost:3000/events/${eventId}`)
      .then((response) => {
        setEventDetails(response.data);
        setEditedEvent(response.data); // Initialize editedEvent with the current data
      })
      .catch((error) => {
        console.error("Error fetching event details:", error);
      });
  }, [eventId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedEvent({
      ...editedEvent,
      [name]: value,
    });
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();

    // Send a PUT request to update the event
    axios
      .put(`http://localhost:3000/events/${eventId}`, editedEvent)
      .then((response) => {
        // Handle the success response from the server
        console.log("Event updated successfully:", response.data);

        // Update the event details on this page
        setEventDetails(response.data);

        // Trigger a refresh of events on the EventsPage
        refreshEvents();

        // Optionally, you can navigate back to the EventsPage here
      })
      .catch((error) => {
        console.error("Error updating event:", error);
      });
  };

  const handleEditClick = (eventDetails) => {
    // Populate the editedEvent state with the current event details when the "Edit" button is clicked
    setEditedEvent({ ...eventDetails });
  };

  return (
    <div>
      <h2>Event Details</h2>
      <img
        src={eventDetails.image}
        alt={eventDetails.title}
        style={{ width: "200px", height: "150px" }}
      />

      <form onSubmit={handleEditSubmit}>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={editedEvent.title}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            name="description"
            value={editedEvent.description}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Location:
          <input
            type="text"
            name="location"
            value={editedEvent.location}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Start Time:
          <input
            type="datetime-local"
            name="startTime"
            value={editedEvent.startTime}
            onChange={handleInputChange}
          />
        </label>
        <label>
          End Time:
          <input
            type="datetime-local"
            name="endTime"
            value={editedEvent.endTime}
            onChange={handleInputChange}
          />
        </label>
        <button type="submit">Save</button>
      </form>
    </div>
  );
}

export default EventDetailsPage;
