import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function EventDetailsPage() {
  const { eventId } = useParams();
  const [eventDetails, setEventDetails] = useState({});
  const [editedEvent, setEditedEvent] = useState({});

  useEffect(() => {
    // Fetch event details based on the eventId
    axios
      .get(`http://localhost:3000/events/${eventId}`)
      .then((response) => {
        setEventDetails(response.data);
        setEditedEvent(response.data); // Initialize editedEvent with the current data
      })
      .catch((error) => {
        console.error("Error fetching event details:", error);
      });
  }, [eventId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedEvent({
      ...editedEvent,
      [name]: value,
    });
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();

    // Send a PUT request to update the event
    axios
      .put(`http://localhost:3000/events/${eventId}`, editedEvent)
      .then((response) => {
        // Handle the success response from the server
        console.log("Event updated successfully:", response.data);

        // Update the event details on this page
        setEventDetails(response.data);
      })
      .catch((error) => {
        console.error("Error updating event:", error);
      });
  };

  return (
    <div>
      <h2>Event Details</h2>
      <img
        src={eventDetails.image}
        alt={eventDetails.title}
        style={{ width: "200px", height: "150px" }}
      />

      <p>Title: {eventDetails.title}</p>
      <p>Description: {eventDetails.description}</p>
      <p>Location: {eventDetails.location}</p>
      <p>startTime: {eventDetails.startTime}</p>
      <p>endTime: {eventDetails.endTime}</p>

      {/* Add more details as needed */}

      <button onClick={() => handleEditClick(eventDetails)}>Edit</button>

      {editedEvent && (
        <form onSubmit={handleEditSubmit}>
          <label>
            Title:
            <input
              type="text"
              name="title"
              value={editedEvent.title}
              onChange={handleInputChange}
            />
          </label>
          {/* Repeat for other fields */}
          <button type="submit">Save</button>
        </form>
      )}
    </div>
  );
}

export default EventDetailsPage;
