import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import axios from "axios";

function EventDetailsPage() {
  const { eventId } = useParams();
  const history = useHistory();
  const [eventDetails, setEventDetails] = useState({});
  const [deleteMessage, setDeleteMessage] = useState("");

  useEffect(() => {
    // Fetch event details based on the eventId
    axios
      .get(`http://localhost:3000/events/${eventId}`)
      .then((response) => {
        setEventDetails(response.data);
      })
      .catch((error) => {
        console.error("Error fetching event details:", error);
      });
  }, [eventId]);

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      // Send a DELETE request to the backend
      axios
        .delete(`http://localhost:3000/events/${eventId}`)
        .then((response) => {
          // Assuming the delete request was successful, you can handle the response here
          console.log("Event deleted successfully");
          // Clear the event details in the state
          setEventDetails({});
          // Display a delete message
          setDeleteMessage("Event deleted successfully.");
          // Redirect to a different page or handle success as needed.
          history.push("/"); // Redirect to the home page or another appropriate page
        })
        .catch((error) => {
          console.error("Error deleting event:", error);
          // Display an error message if the delete request fails
          setDeleteMessage("Error deleting event. Please try again.");
        });
    }
  };

  return (
    <div>
      <h2>Event Details</h2>
      {deleteMessage && <p className="delete-message">{deleteMessage}</p>}
      <img
        src={eventDetails.image}
        alt={eventDetails.title}
        style={{ width: "200px", height: "150px" }}
      />

      <p>Title: {eventDetails.title}</p>
      <p>Description: {eventDetails.description}</p>
      <p>Location: {eventDetails.location}</p>
      <p>startTime: {eventDetails.startTime}</p>
      <p>endTime: {eventDetails.endTime}</p>

      <button onClick={handleDelete}>Delete Event</button>
    </div>
  );
}

export default EventDetailsPage;
