import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function EventDetailsPage() {
  const { eventId } = useParams();
  const [eventDetails, setEventDetails] = useState({});
  const [editedEvent, setEditedEvent] = useState({});
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [isEditMode, setIsEditMode] = useState(false);

  useEffect(() => {
    // Fetch event details based on the eventId
    axios
      .get(`http://localhost:3000/events/${eventId}`)
      .then((response) => {
        setEventDetails(response.data);
        setEditedEvent(response.data); // Initialize editedEvent with the current data
      })
      .catch((error) => {
        console.error("Error fetching event details:", error);
      });
  }, [eventId]);

  const handleEditSubmit = (e) => {
    e.preventDefault();

    // Send a PUT request to update the event
    axios
      .put(`http://localhost:3000/events/${eventId}`, editedEvent)
      .then((response) => {
        // Handle the success response from the server
        console.log("Event updated successfully:", response.data);
        setSuccessMessage("Event updated successfully");

        // Update the event details on this page
        setEventDetails(response.data);

        // Exit edit mode after saving
        setIsEditMode(false);
      })
      .catch((error) => {
        console.error("Error updating event:", error);
        setErrorMessage("Error updating event");
      });
  };

  const handleEditClick = () => {
    setIsEditMode(!isEditMode);
  };

  const handleDelete = () => {
    // Send a DELETE request to delete the event
    axios
      .delete(`http://localhost:3000/events/${eventId}`)
      .then(() => {
        // Handle the success response from the server
        console.log("Event deleted successfully");
        setSuccessMessage("Event deleted successfully");

        // Optionally, you can navigate to another page after successful deletion
        // For example: history.push('/events');
      })
      .catch((error) => {
        console.error("Error deleting event:", error);
        setErrorMessage("Error deleting event");
      });
  };

  return (
    <div>
      <h2>Event Details</h2>
      <img
        src={eventDetails.image}
        alt={eventDetails.title}
        style={{ width: "200px", height: "150px" }}
      />

      {successMessage && (
        <div className="success-message">{successMessage}</div>
      )}
      {errorMessage && <div className="error-message">{errorMessage}</div>}

      {!isEditMode && <button onClick={handleEditClick}>Edit</button>}

      {isEditMode ? (
        <form onSubmit={handleEditSubmit}>
          {/* ... Input fields ... */}
          <button type="submit">Save</button>
        </form>
      ) : (
        <div>
          <p>Title: {eventDetails.title}</p>
          <p>Date: {eventDetails.date}</p>
          {/* Add more details as needed */}
        </div>
      )}

      <button onClick={handleDelete}>Delete</button>
    </div>
  );
}

export default EventDetailsPage;
